    for p in production_uom_definition:
        if doc.stock_uom == p.uom:
            frappe.throw("You Can Not Add Default Unit of Measure In Table 'Production UOM Definition'")
        else:
            uoms_conversion_details = doc.get("uoms" ,filters = {"uom": p.uom })
            if uoms_conversion_details:
                for ucd in uoms_conversion_details:
                    if ucd.uom == p.uom:
                        ucd.conversion_factor = (1/p.value_per_unit)
            else:
                doc.append("uoms",{
					'uom': p.uom ,
					'conversion_factor': (1/p.value_per_unit),
				},),

